create procedure dbo.USP_DATAFORMTEMPLATE_ADD_COUNTRY_EXTENSION ( @ID uniqueidentifier = null output, @CHANGEAGENTID uniqueidentifier = null, @FULLCOUNTRYNAME nvarchar(50) = '', @COUNTRYCODE nvarchar(2) = '', @COUNTRYSTATUSCODEID uniqueidentifier = null, @COUNTRYSTATUSDATE datetime = null, @COUNTRYREGIONCODEID uniqueidentifier = null ) as
set nocount on;
if @ID is null set @ID = newid()
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
declare @countryOfficeConstituencyCode uniqueidentifier
begin try select @countryOfficeConstituencyCode = ID from CONSTITUENCYCODE where lower(DESCRIPTION) = 'country office' if @countryOfficeConstituencyCode is null RAISERROR 1000000 'Could not find Country Office constituency code'
exec dbo.USP_ORGANIZATION_ADD @ID output, @CHANGEAGENTID, @CHANGEAGENTID, @FULLCOUNTRYNAME, '', --@WEBADDRESS NULL, --@PICTURE, NULL, --@PICTURETHUMBNAIL, NULL, --@INDUSTRYCODEID, NULL, --@NUMEMPLOYEES, NULL, --@NUMSUBSIDIARIES, NULL, --@PARENTCORPID, NULL, --@ADDRESS_ADDRESSTYPECODEID, 0, --@ADDRESS_DONOTMAIL, NULL, --@ADDRESS_COUNTRYID, NULL, --@ADDRESS_STATEID, '', --@ADDRESS_ADDRESSBLOCK, '', --@ADDRESS_CITY, '', --@ADDRESS_POSTCODE, NULL, --@PHONE_PHONETYPECODEID, NULL, --@PHONE_NUMBER, NULL, --@SKIP_ADDING_SECURITYGROUPS, NULL, --@EMAILADDRESS_EMAILADDRESSTYPECODEID, NULL, --@EMAILADDRESS_EMAILADDRESS, 0, --@OMITFROMVALIDATION, '', --@CART, '', --@DPC, '', --@LOT, NULL, --@COUNTYCODEID, NULL, --@CONGRESSIONALDISTRICTCODEID, NULL, --@LASTVALIDATIONATTEMPTDATE, '', --@VALIDATIONMESSAGE, 0, --@CERTIFICATIONDATA, NULL, --@ADDRESS_DONOTMAILREASONCODEID, 1, --@ISPRIMARY, NULL, --@SKIP_ADDING_SITES, NULL, --@INFOSOURCECODEID, 1;
-- Change the LookupID to the one entered by the user update CONSTITUENT set CUSTOMIDENTIFIER = @COUNTRYCODE where ID = @ID;
-- Add the Child Project constituency code if @countryOfficeConstituencyCode is not null exec dbo.USP_DATAFORMTEMPLATE_ADD_CONSTITUENCY @CHANGEAGENTID = @CHANGEAGENTID, @CONSTITUENTID = @ID, @CONSTITUENCYCODEID = @countryOfficeConstituencyCode;
-- handle inserting the data insert into dbo.USR_COUNTRYEXTENSION (ID, COUNTRYSTATUSCODEID, COUNTRYSTATUSDATE, COUNTRYREGIONCODEID, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED) values (@ID, @COUNTRYSTATUSCODEID, @COUNTRYSTATUSDATE, @COUNTRYREGIONCODEID, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE) end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0
