SPDataForm.common:CreateProcedureSQL:

create procedure dbo.USP_DATAFORMTEMPLATE_VIEW_COUNTRY_EXTENSION
(
	@ID uniqueidentifier,
	@DATALOADED bit = 0 output,
	@FULLCOUNTRYNAME nvarchar(50) = null output,
    @COUNTRYCODE nvarchar(2) = null output,
    @COUNTRYSTATUSCODE nvarchar(100) = null output,
    @COUNTRYSTATUSDATE datetime = null output,
    @COUNTRYREGIONCODE nvarchar(100) = null output
)
as
	set nocount on;

	-- be sure to set this, in case the select returns no rows
	set @DATALOADED = 0;

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.
	select @DATALOADED = 1,
		   @FULLCOUNTRYNAME = FULLCOUNTRYNAME, 
           @COUNTRYCODE = COUNTRYCODE, 
           @COUNTRYSTATUSCODE = (select DESCRIPTION from dbo.USR_COUNTRYSTATUSCODE where ID = COUNTRYSTATUSCODEID), 
           @COUNTRYSTATUSDATE = COUNTRYSTATUSDATE, 
           @COUNTRYREGIONCODE = (select DESCRIPTION from dbo.USR_COUNTRYREGIONCODE where ID = COUNTRYREGIONCODEID)
	 from dbo.USR_COUNTRYEXTENSION
	where ID = @ID

	return 0;
