                                    @FULLCOUNTRYNAME, 		
                                    '', --@WEBADDRESS
                                    NULL, --@PICTURE, 
                                    NULL, --@PICTURETHUMBNAIL, 
                                    NULL, --@INDUSTRYCODEID,
                                    NULL, --@NUMEMPLOYEES,
                                    NULL, --@NUMSUBSIDIARIES,
